function seccion_location(){var ventana_alto=$(window).height();var ventana_ancho=$(window).width();var ventana_media=$(window).height()/1.2;var slide=$(".container").width();$('.slide-home').css('min-height',ventana_alto);$('.slide-home').css('width',slide);$('.home-item').css('min-height',ventana_alto);$('.home').css('min-height',ventana_alto)}
function Home(){var owl=$(".slide-home");owl.owlCarousel({autoPlay:!0,items:1,itemsTablet:[600,1],itemsMobile:[400,1],pagination:!1})}
function menu(){$(".open-menu").click(function(e){$('.header').css('left','0');$('.cerrar-menu').css('left','0')})
$(".cerrar-menu").click(function(e){$('.header').css('left','-100%');$('.cerrar-menu').css('left','-100%')})}
$(document).ready(function(){seccion_location();Home();menu()})